/**
 * eidtorEnhancement.js by Mr.Raindrop 2015-02-04
 * use keyboard shortcuts for typecho post & page editor.
 * ctrl+s : save draft
 * ctrl+Enter : publish post
 *
 **/

$(function(){var e=false,t=.1,n=$("#btn-save"),r=$("#btn-submit"),i=83,s=13;$(document).on("keydown",function(o){var u=o.keyCode||o.which;if(o.ctrlKey&&(u===i||u===s)){if(e)return;e=true;o.preventDefault();o.stopPropagation();switch(u){case i:n.trigger("click");break;case s:r.trigger("click");break;default:break}setTimeout(function(){e=false},t*1e3)}})})